pub enum Error<CommE, PinE> {

    /// Communication error
    Comm(CommE),

    /// Pin setting error
    Pin(PinE),
}

pub enum AddrMode {

    /// Horizontal mode
    Horizontal = ,

    /// Vertical mode
    Vertical = ,

    /// Page mode (default)
    Page = ,
}

pub enum DisplayRotation {

    /// No rotation, normal display
    Rotate0,

    /// Rotate by 90 degress clockwise
    Rotate90,

    /// Rotate by 180 degress clockwise
    Rotate180,

    /// Rotate 270 degress clockwise
    Rotate270,
}

pub enum DisplaySize {

    /// 128 by 64 pixels
    Display128x64,

    /// 128 by 32 pixels
    Display128x32,

    /// 96 by 16 pixels
    Display96x16,

    /// 70 by 42 pixels
    Display72x40,

    /// 64 by 48 pixels
    Display64x48,
}

pub enum TerminalModeError<DI> where DI: DisplayInterface {

    /// An error occurred in the underlying interface layer
    InterfaceError(DI::Error),

    /// The mode was used before it was initialized
    Uninitialized,

    /// A location was specified outside the bounds of the screen
    OutOfBounds,
}
